'use strict';

/**
 * JWT authentication middleware.
 * @author Francisco Buceta
 * @version 1.0.8
 */

const async = require('async'),
  errors = require('restify-errors'),
  jwt = require('jsonwebtoken'),
  unless = require('express-unless');

const DEFAULT_REVOKED_FUNCTION = (_, __, cb) => {
  return cb(null, false);
};

function isFunction(object) {
  return Object.prototype.toString.call(object) === '[object Function]';
}

function wrapStaticSecretInCallback(secret) {
  return (_, __, cb) => {
    return cb(null, secret);
  };
}

module.exports = (options) => {
  if (!options || !options.secret) throw new Error('secret should be set');

  let secretCallback = options.secret;

  if (!isFunction(secretCallback)) {
    secretCallback = wrapStaticSecretInCallback(secretCallback);
  }

  const isRevokedCallback = options.isRevoked || DEFAULT_REVOKED_FUNCTION;

  const _requestProperty = options.userProperty || options.requestProperty || 'user';
  const credentialsRequired = typeof options.credentialsRequired === 'undefined' ? true : options.credentialsRequired;

  const middleware = (req, res, next) => {
    let token;

    if (req.method === 'OPTIONS' && req.headers.hasOwnProperty('access-control-request-headers')) {
      const hasAuthInAccessControl = !!~req.headers['access-control-request-headers']
        .split(',')
        .map((header) => {
          return header.trim();
        })
        .indexOf('authorization');

      if (hasAuthInAccessControl) {
        return next();
      }
    }

    if (options.getToken && typeof options.getToken === 'function') {
      try {
        token = options.getToken(req);
      } catch (e) {
        return next(e);
      }
    } else if (req.headers && req.headers.authorization) {
      const parts = req.headers.authorization.split(' ');
      if (parts.length === 2) {
        const scheme = parts[0];
        const credentials = parts[1];

        if (/^(?:Bearer|JWT)$/i.test(scheme)) {
          token = credentials;
        } else {
          return next(new errors.InvalidCredentialsError('Formato do token válido é Authorization: Bearer [token] or Jwt [token]'));
        }
      } else {
        return next(new errors.InvalidCredentialsError('Formato do token válido é Authorization: Bearer [token] or Jwt [token]'));
      }
    }

    if (!token) {
      if (credentialsRequired) {
        return next(new errors.InvalidCredentialsError('Não foi encontrado nenhum token de autorização para utilização da API'));
      } else {
        return next();
      }
    }

    let dtoken;

    try {
      dtoken = jwt.decode(token, {complete: true}) || {};
    } catch (e) {
      return next(new errors.InvalidCredentialsError('O token está corrompido'))
    }

    async.parallel([
      (callback) => { // getSecret
        const arity = secretCallback.length;
        if (arity === 4) {
          secretCallback(req, dtoken.header, dtoken.payload, callback);
        } else { // arity == 3
          secretCallback(req, dtoken.payload, callback);
        }
      },
      (callback) => { // checkRevoked
        isRevokedCallback(req, dtoken.payload, callback);
      }
    ], (err, results) => {
      if (err) {
        return next(err);
      }
      const revoked = results[1];
      if (revoked) {
        return next(new errors.UnauthorizedError('O token foi revogado.'));
      }

      const secret = results[0];

      jwt.verify(token, secret, options, (err, decoded) => {
        if (err && credentialsRequired) {
          return (err.name === 'TokenExpiredError') ?
            next(new errors.UnauthorizedError('O token informado está expirado, favor obter um novo.')) :
            next(new errors.InvalidCredentialsError(err));
        }
        req[_requestProperty] = decoded;
        next();
      });
    });
  };

  middleware.unless = unless;

  return middleware;
};
